import sailpoint.object.Identity; 
import sailpoint.tools.Util;

int employeenumber = Util.atoi(identity.getName());
String type = identity.getAttribute("employeeType");
String dn="";
if(Util.isNotNullOrEmpty(type) && type.equalsIgnoreCase("Employee")){
dn="cn="+"E"+Util.itoa(employeenumber)+",ou=employees,ou=users,dc=example,dc=com";
}else{

dn="cn="+"C"+Util.itoa(employeenumber)+",ou=contractors,ou=users,dc=example,dc=com";
}
return dn;
